import{_ as e,c as s,o as a,a4 as t}from"./chunks/framework.ttppV29E.js";const u=JSON.parse('{"title":"MESH Message Task","description":"","frontmatter":{},"headers":[],"relativePath":"docs/endpoints/mesh-message.md","filePath":"docs/endpoints/mesh-message.md","lastUpdated":1755869854000}'),o={name:"docs/endpoints/mesh-message.md"},n=t('<h1 id="mesh-message-task" tabindex="-1">MESH Message Task <a class="header-anchor" href="#mesh-message-task" aria-label="Permalink to &quot;MESH Message Task&quot;">â€‹</a></h1><p><a href="https://digital.nhs.uk/services/message-exchange-for-social-care-and-health-mesh" target="_blank" rel="noreferrer">MESH (<strong>M</strong>essage <strong>E</strong>xchange for <strong>S</strong>ocial Care and <strong>H</strong>ealth)</a> is a messaging system used within the NHS for secure messaging and file transfer. The MESH Message task allows you to send a message using this system in much the same way you might send an email.</p><p>In order to send a MESH message, you will have to provide details of your account. AireGlu does not offer a default MESH sender. You will need to enter your Mailbox name and password. <a href="/docs/getting-started/secrets.html">You can store these as secrets</a> for security.</p><p>You will also need to know the recipient&#39;s mailbox name and the Workflow ID. Like other data, these can be entered, or pulled from the context panel.</p><p>Finally, you need to create your message. This should be plaintext, and can include data from the context panel.</p><p>Please note: AireGlu does not offer the option to send attachments through MESH.</p>',6),r=[n];function d(i,l,h,c,m,g){return a(),s("div",null,r)}const _=e(o,[["render",d]]);export{u as __pageData,_ as default};
